import React, {useState, useEffect} from 'react';
import {withFormik, Form, Field} from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import styled from 'styled-components';

const PostPage = styled.div`
    background-color: #52ad9c;
    color: 347624f;
    width: 90%;
    margin: 0 auto;
    border: 4px solid #47624f;
    border-radius: 10px;
    height: 40vh;
`;
const BoxField = styled(Field)`
    padding: 1%;
    margin: 1%;
    border: 2px solid black;
    width: 20%;
`;
const CenterForm = styled.h1`
    margin-top: 5%;
`;
const Button = styled.button`
    margin: 1% 0% 4% 0%;
    padding: 1%
`;
const CardArea = styled.div`
    margin-left: 6%;
    display: flex;
    flex-wrap: wrap;
`;
const PostCards = styled.div`
    background-color: white;
    color: black;
    border: 2px solid black;
    border-radius: 5%;
    width: 20%;
    margin: 2%;
    padding: 2%;
`;

const ChefPosting = ({values, touched, errors, status}) => {
    const [posts, setPosts] = useState([]);
    useEffect(() => {
        status && setPosts(posts => [...posts, status])
    }, [status])
// image
    return (
        <PostPage>
            <CenterForm>Make New Post</CenterForm>
            <Form>
                <BoxField
                type='text'
                name='title'
                placeholder='title/name of dish'
                />
                {touched.title && errors.title && (
                    <p>{errors.title}</p>
                )}<br />
                <BoxField component='select' name='mealType'>
                    <option>Please Choose an Option</option>
                    <option>Breakfast</option>
                    <option>Lunch</option>
                    <option>Dinner</option>
                    <option>Snack</option>
                    <option>Dessert</option>
                </BoxField>
                {touched.mealType && errors.mealType && (
                    <p>{errors.mealType}</p>
                )}<br />
                <BoxField
                component='textarea'
                type='text'
                name='ingredients'
                placeholder='ingredients'
                /><br />
                <BoxField
                component='textarea'
                type='text'
                name='instructions'
                placeholder='instructions'
                /><br />
                <Button type='submit'>Post</Button>
            </Form>
            <CardArea>
                {posts.map(post => (
                    <PostCards key={post.id}>
                        <p>Dish: {post.title}</p>
                        <p>Meal Type: {post.mealType}</p>
                        <p>Ingredients: {post.ingredients}</p>
                        <p>Instructions: {post.instructions}</p>
                    </PostCards>
                ))}
            </CardArea>
        </PostPage>
    )
}



const FormikChefPosting = withFormik({
    mapPropsToValues({title, mealType}) {
        return {
            title: title || '',
            mealType: mealType || '',
        };
    },
    validationSchema: Yup.object().shape({
        title: Yup.string().required('Title is a required field'),
        mealType: Yup.string()
            .oneOf(['Breakfast', 'Lunch', 'Dinner', 'Snack', 'Dessert'])
            .required('Please select one')
    }),
    handleSubmit(values, {setStatus}) {
        axios
            .post('https://reqres.in/api/users', values)
            .then(res => {setStatus(res.data);})
            .catch(err => console.log(err.response));
    }
})(ChefPosting);
export default FormikChefPosting;
