import React, {useState, useEffect} from 'react';
import {withFormik, Form, Field} from 'formik';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import * as Yup from 'yup';
import axios from 'axios';
import chefPosting from './ChefLogin';

const ChefOnboarding = ({values, touched, errors, status}) => {
    const [chefs, setChefs] = useState([]);
    useEffect(() => {
        status && setChefs(chefs => [...chefs, status])
    }, [status])

    return (
        <div>
            <h1>Login / Create New Chef Profile</h1>
            <Form>
                <Field
                type='text'
                name='name'
                placeholder='name'
                />
                {touched.name && errors.name && (
                    <p>{errors.name}</p>
                )}<br />
                <Field
                type='text'
                name='email'
                placeholder='email'
                />
                {touched.email && errors.email && (
                    <p>{errors.email}</p>
                )}<br />
                <Field
                type='password'
                name='password'
                placeholder='password'
                />
                {touched.password && errors.password && (
                    <p>{errors.password}</p>
                )}<br />
                <h2>Terms Of Service</h2>
                <Field
                type='checkbox'
                name='termsOfService'
                checked={values.termsOfService}
                /><br />
                <Router>
                   {/* <Route exact path='/chefposts' component={chefPosting} />
                   <button type='submit' onClick={chefPosting}>Login</button> */}

                   <button type='submit' onClick={<Route exact path='/chefposts' component={chefPosting} />}>Login</button>
                </Router>
                {/* <button type='submit'>Login</button> */}
            </Form>
            {/* {chefs.map(chef => (
                <div key={chef.id}>
                    <p>Name: {chef.name}</p>
                    <p>Email: {chef.email}</p>
                    <p>Password Length: {chef.password.length}</p>
                </div>
            ))} */}
        </div>
    )
}



const FormikChefOnboarding = withFormik({
    mapPropsToValues({name, email, password, termsOfService}) {
        return {
            name: name || '',
            email: email || '',
            password: password || '',
            termsOfService: termsOfService || false
        };
    },
    validationSchema: Yup.object().shape({
        name: Yup.string().required('Name is a required field'),
        email: Yup.string().required('Email is a required field'),
        password: Yup.string().required('Password is a required field'),
    }),
    handleSubmit(values, {setStatus}) {
        axios
            .post('https://reqres.in/api/users', values)
            .then(res => {setStatus(res.data);})
            .catch(err => console.log(err.response));
    }
})(ChefOnboarding);
export default FormikChefOnboarding;
